import React from 'react';
import { useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import useMediaQuery from '@mui/material/useMediaQuery';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';

const Hero = () => {
  const theme = useTheme();
  const isMd = useMediaQuery(theme.breakpoints.up('md'), {
    defaultMatches: true,
  });

  return (
    <Grid
      container
      spacing={4}
    >
      <Grid
        item
        xs={12}
        md={4}
        sx={{
          height: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
        }}
      >
        <Box data-aos={isMd ? 'fade-right' : 'fade-up'}>
          <Box marginBottom={2}>                 
            <Typography
              variant="h2"
              sx={{
                fontWeight: 700,
              }}
            >
              Create your beautiful website today.
            </Typography>
          </Box>
          <Box marginBottom={3}>
            <Typography variant="h6" component="p" color="textSecondary">
              Make your business the centre of attention.
              <br />
              All the help and tools you need to be online.
            </Typography>
          </Box>
          <Box
            display="flex"
            flexDirection={{ xs: 'column', sm: 'row' }}
            alignItems={{ xs: 'stretched', sm: 'flex-start' }}
          >
            <Box
              component={Button}
              variant="outlined"
              color="primary"
              size="large"
              fullWidth={isMd ? false : true}
            >
              Find out more
            </Box>
            <Box
              component={Button}
              variant="contained"
              color="primary"
              size="large"
              fullWidth={isMd ? false : true}
              marginTop={{ xs: 1, sm: 0 }}
              marginLeft={{ sm: 2 }}
            >
              Start for free
            </Box>
          </Box>
        </Box>
      </Grid>
      {isMd ? (
        <Grid
          item
          xs={12}
          md={8}
          sx={{
            display: 'flex',
            flexWrap: 'no-wrap !important',
          }}
        >
          <Box
            component={'img'}
            src={'https://assets.maccarianagency.com/backgrounds/bg-1.png'}
            alt="..."
            width={486}
            sx={{
              maxWidth: '100%',
              marginTop: '-3rem',
            }}
          />
          <Box
            component={'img'}
            src={'https://assets.maccarianagency.com/backgrounds/bg-2.png'}
            alt="..."
            width={486}
            sx={{
              maxWidth: '100%',
              marginTop: '-3rem',
            }}
          />
          <Box
            component={'img'}
            src={'https://assets.maccarianagency.com/backgrounds/bg-1.png'}
            alt="..."
            width={486}
            sx={{
              maxWidth: '100%',
              marginTop: '-3rem',
            }}
          />
        </Grid>
      ) : null}
    </Grid>
  );
};

export default Hero;
