import React from 'react';
import { useTheme } from '@mui/material/styles';
import useMediaQuery from '@mui/material/useMediaQuery';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import { dark } from 'theme/colors';

const Hero = () => {
  const theme = useTheme();
  const isMd = useMediaQuery(theme.breakpoints.up('md'), {
    defaultMatches: true,
  });

  return (
    <Grid container spacing={4}>
      <Grid
        item
        xs={12}
        md={4}
        sx={{
          height: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
        }}
      >
        <Box data-aos={isMd ? 'fade-right' : 'fade-up'}>
          <Box marginBottom={2}>
            <Typography
              variant="h2"
              color="textPrimary"
              sx={{
                fontWeight: 700,
                color: dark.text.primary,
              }}
            >
              Turn your ideas into a success
            </Typography>
          </Box>
          <Box marginBottom={3}>
            <Typography
              variant="h6"
              component="p"
              color="textSecondary"
              sx={{
                fontWeight: 400,
                color: dark.text.secondary,
              }}
            >
              Webbee will make your product look modern and professional while
              saving you precious time.
            </Typography>
          </Box>
          <Box
            display="flex"
            flexDirection={{ xs: 'column', sm: 'row' }}
            alignItems={{ xs: 'stretched', sm: 'flex-start' }}
          >
            <Button
              component={'a'}
              variant="contained"
              color="primary"
              size="large"
              fullWidth={isMd ? false : true}
              href={
                'https://material-ui.com/store/items/webbee-landing-page/'
              }
              target={'_blank'}
            >
              Purchase now
            </Button>
            <Box
              marginTop={{ xs: 2, sm: 0 }}
              marginLeft={{ sm: 2 }}
              width={{ xs: '100%', md: 'auto' }}
            >
              <Button
                component={'a'}
                href={'/docs-introduction'}
                variant="outlined"
                color="primary"
                size="large"
                fullWidth={isMd ? false : true}
              >
                View documentation
              </Button>
            </Box>
          </Box>
        </Box>
      </Grid>
      {isMd ? (
        <Grid
          item
          xs={12}
          md={8}
          sx={{
            display: 'flex',
            flexWrap: 'no-wrap !important',
          }}
        >
          <Box
            component={'img'}
            src={'https://assets.maccarianagency.com/backgrounds/bg-1.png'}
            alt="..."
            width={486}
            sx={{
              maxWidth: '100%',
              marginTop: '-3rem',
            }}
          />
          <Box
            component={'img'}
            src={'https://assets.maccarianagency.com/backgrounds/bg-2.png'}
            alt="..."
            width={486}
            sx={{
              maxWidth: '100%',
              marginTop: '-3rem',
            }}
          />
          <Box
            component={'img'}
            src={'https://assets.maccarianagency.com/backgrounds/bg-1.png'}
            alt="..."
            width={486}
            sx={{
              maxWidth: '100%',
              marginTop: '-3rem',
            }}
          />
        </Grid>
      ) : null}
    </Grid>
  );
};

export default Hero;
