import React, { useEffect, useRef } from 'react';

const FreshworksForm = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://northernlionlabs.myfreshworks.com/crm/sales/web_forms/878096a47237ef12a6f963047c297e7e09645dcba8490d9b0e631e7eab0e9385/form.js';
    script.async = true;
    script.crossOrigin = 'anonymous';
    script.id = 'fs_878096a47237ef12a6f963047c297e7e09645dcba8490d9b0e631e7eab0e9385';

    // Only append the script if it hasn't been added yet
    if (!document.getElementById(script.id)) {
      document.body.appendChild(script);
    }

    // Create a MutationObserver to watch for the form being added to the DOM
    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (mutation.type === 'childList') {
          mutation.addedNodes.forEach((node) => {
            if (node.id === 'freshworks-container') {
              // Move the form into our React component's container
              if (containerRef.current && node.parentElement !== containerRef.current) {
                containerRef.current.appendChild(node);
                observer.disconnect(); // Stop observing once we've moved the form
              }
            }
          });
        }
      });
    });

    // Start observing the document with the configured parameters
    observer.observe(document.body, { childList: true, subtree: true });

    // Clean up function
    return () => {
      observer.disconnect();
      // Only remove the script if our component created it
      const existingScript = document.getElementById(script.id);
      if (existingScript && existingScript.parentNode === document.body) {
        document.body.removeChild(existingScript);
      }
    };
  }, []);

  return <div ref={containerRef} style={{ width: '100%', minHeight: '400px' }} />;
};

export default FreshworksForm;
