import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { useTheme } from '@mui/material/styles';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Link from '@mui/material/Link';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import WebbeeLogo from "../../../../../../svg/logos/Webbee";

const SidebarNav = ({
                      pages,
                      onClose,
                      backgroundColor,
                      textColor,
                      activeColor,
                      hoverColor
                    }) => {
  const theme = useTheme();
  const [activeLink, setActiveLink] = useState('');

  useEffect(() => {
    setActiveLink(window && window.location ? window.location.pathname : '');
  }, []);

  // Use provided colors or fall back to theme defaults
  const bgColor = backgroundColor || theme.palette.background.paper;
  const txtColor = textColor || theme.palette.text.primary;
  const actColor = activeColor || theme.palette.primary.main;
  const hovColor = hoverColor || theme.palette.primary.dark;

  return (
    <Box sx={{ backgroundColor: bgColor, color: txtColor }}>
      <Box
        display={'flex'}
        justifyContent={'flex-end'}
        onClick={() => onClose()}
      >
        <IconButton>
          <CloseIcon fontSize="small" sx={{ color: txtColor }} />
        </IconButton>
      </Box>
      <Box paddingX={2} paddingBottom={2}>
        <Box>
          {pages.map((item, i) => (
            <Box key={i} marginBottom={4}>
              <Box
                display={'flex'}
                alignItems="baseline"
                component="a"
                underline="none"
                href="/"
                title="Northern Lion Labs"
                width={35}
              >
                <WebbeeLogo height={'180%'} width={'180%'} />
              </Box>
              <Typography
                variant="caption"
                sx={{
                  fontWeight: 700,
                  textTransform: 'uppercase',
                  marginBottom: 1,
                  display: 'block',
                  color: txtColor,
                }}
              >
                {item.title}
              </Typography>
              <Grid container spacing={1}>
                {item.pages.map((p, i) => (
                  <Grid item xs={6} key={i}>
                    <Link
                      variant="body2"
                      component={'a'}
                      href={p.href}
                      underline={'none'}
                      sx={{
                        fontWeight: activeLink === p.href ? 600 : 400,
                        color: activeLink === p.href ? actColor : txtColor,
                        '&:hover': {
                          textDecoration: 'none',
                          color: hovColor,
                        },
                      }}
                      onClick={() => onClose()}
                    >
                      {p.title}
                    </Link>
                  </Grid>
                ))}
              </Grid>
            </Box>
          ))}
        </Box>
        {/* Commented out Button sections remain unchanged */}
      </Box>
    </Box>
  );
};

SidebarNav.propTypes = {
  pages: PropTypes.array.isRequired,
  onClose: PropTypes.func,
  backgroundColor: PropTypes.string,
  textColor: PropTypes.string,
  activeColor: PropTypes.string,
  hoverColor: PropTypes.string,
};

export default SidebarNav;
