import React from 'react';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const PrivacySection = ({ title, description }) => {
  return (
    <Box>
      <Typography
        variant={'h6'}
        gutterBottom
        sx={{
          fontWeight: 'medium',
        }}
      >
        {title}
      </Typography>
      <Typography component={'p'} color={'textSecondary'}>
        {description}
      </Typography>
    </Box>
  );
};

PrivacySection.propTypes = {
  title: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
};

const Content = () => {
  const data = [
    {
      title: '1. Introduction',
      description:
        'Welcome to Northern Lion Labs llc ("we," "our," or "us"). We respect your privacy and are committed to protecting your personal data. This privacy policy will inform you about how we look after your personal data when you visit our website and tell you about your privacy rights and how the law protects you.',
    },
    {
      title: '2. The Data We Collect About You',
      description:
        'We may collect, use, store and transfer different kinds of personal data about you which we have grouped together as follows:\n' +
        '\n' +
        'Identity Data includes first name, last name.\n' +
        'Contact Data includes email address.\n' +
        'Technical Data includes internet protocol (IP) address, browser type and version, time zone setting and location, browser plug-in types and versions, operating system and platform, and other technology on the devices you use to access this website.\n' +
        'Usage Data includes information about how you use our website and services.',
    },
    {
      title: '3. How We Use Your Personal Data',
      description:
        'We will only use your personal data when the law allows us to. Most commonly, we will use your personal data in the following circumstances:\n' +
        '\n' +
        'To respond to your inquiries.\n' +
        'To provide you with information, products, or services that you request from us.\n' +
        'To notify you about changes to our service.\n' +
        'To administer and protect our business and this website.\n',
    },
    {
      title: '4. Data Security',
      description:
        'We have put in place appropriate security measures to prevent your personal data from being accidentally lost, used or accessed in an unauthorized way, altered or disclosed. In addition, we limit access to your personal data to those employees, agents, contractors and other third parties who have a business need to know.',
    },
    {
      title: '5. Accuracy of Materials',
      description:
        'The materials appearing on Northern Lion Labs\'s website could include technical, typographical, or photographic errors. Northern Lion Labs llc does not warrant that any of the materials on its website are accurate, complete or current. Northern Lion Labs llc may make changes to the materials contained on its website at any time without notice.',
    },
    {
      title: '6. Links',
      description:
        'Northern Lion Labs llc has not reviewed all of the sites linked to its website and is not responsible for the contents of any such linked site. The inclusion of any link does not imply endorsement by Northern Lion Labs llc of the site. Use of any such linked website is at the user\'s own risk.',
    },
    {
      title: '7. Modifications',
      description:
        'Northern Lion Labs llc may revise these terms of service for its website at any time without notice. By using this website you are agreeing to be bound by the then current version of these terms of service.',
    },
    {
      title:
        '8. Indemnification',
      description:
        'You agree to indemnify, defend, and hold harmless Northern Lion Labs llc, its officers, directors, employees, agents, licensors, and suppliers from and against all losses, expenses, damages and costs, including reasonable attorneys\' fees, resulting from any violation of these Terms of Service or any activity related to your account (including negligent or wrongful conduct) by you or any other person accessing the service using your account.',
    },
    {
      title:
        '8. Termination',
      description:
        'We may terminate or suspend access to our service immediately, without prior notice or liability, for any reason whatsoever, including, without limitation, if you breach the Terms. All provisions of the Terms which by their nature should survive termination shall survive termination, including, without limitation, ownership provisions, warranty disclaimers, indemnity, and limitations of liability.',
    },
  ];
  return (
    <Box>
      {data.map((item, i) => (
        <Box key={i} marginBottom={i < data.length - 1 ? 4 : 0}>
          <PrivacySection {...item} />
        </Box>
      ))}
    </Box>
  );
};

export default Content;
