export const light = {
  alternate: {
    main: '#f8f8f8', // Very Light Gray
    dark: '#f3f3f3', // Slightly darker shade of Very Light Gray
  },
  cardShadow: 'rgba(0, 51, 102, .11)', // Based on Deep Blue
  mode: 'light',
  primary: {
    main: '#003366', // Deep Blue
    light: '#008080', // Teal
    dark: '#002244', // Darker shade of Deep Blue
    contrastText: '#fff',
  },
  secondary: {
    light: '#00FF00', // Bright Green (accent)
    main: '#00FF00', // Bright Green (accent)
    dark: '#00CC00', // Slightly darker Bright Green
    contrastText: '#ffffff',
  },
  text: {
    primary: '#333333', // Dark Gray
    secondary: '#666666', // Medium Gray
  },
  divider: 'rgba(0, 0, 0, 0.12)',
  background: {
    paper: '#fff', // White
    default: '#fff', // White
    level2: '#f8f8f8', // Very Light Gray
    level1: '#fff', // White
  },
};

export const dark = {
  alternate: {
    main: 'rgba(255, 255, 255, .04)',
    dark: 'rgba(255, 255, 255, .04)',
  },
  cardShadow: 'rgba(0, 0, 0, .11)',
  common: {
    black: '#000',
    white: '#fff',
  },
  mode: 'dark',
  primary: {
    main: '#00FF00', // Bright Green (accent)
    light: '#008080', // Teal
    dark: '#003366', // Deep Blue
    contrastText: '#111111',
  },
  secondary: {
    light: '#00FF00', // Bright Green (accent)
    main: '#00E600', // Slightly darker Bright Green
    dark: '#00CC00', // Even darker Bright Green
    contrastText: '#111111',
  },
  text: {
    primary: '#ffffff', // White
    secondary: '#d3d3d3', // Light Gray
  },
  divider: 'rgba(255, 255, 255, 0.12)',
  background: {
    paper: '#001a33', // Very Dark Blue
    default: '#001a33', // Very Dark Blue
    level2: '#002b4d', // Slightly lighter Dark Blue
