import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { useTheme } from '@mui/material/styles';
import useMediaQuery from '@mui/material/useMediaQuery';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Link from '@mui/material/Link';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import CloseIcon from '@mui/icons-material/Close';
import WebbeeLogo from "../../../../../../svg/logos/Webbee";

const SidebarNav = ({ pages, onClose }) => {
  const [activeLink, setActiveLink] = useState('');
  const theme = useTheme();
  const isDarkMode = useMediaQuery('(prefers-color-scheme: dark)');

  useEffect(() => {
    setActiveLink(window && window.location ? window.location.pathname : '');
  }, []);

  // Color schemes
  const colors = {
    backgroundColor: isDarkMode ? "#000945" : "#ffffff", // Navy blue in dark mode, white in light mode
    sidebarNavBackgroundColor: isDarkMode ? "#000830" : "#ffffff", // Slightly darker navy in dark mode, same as backgroundColor in light mode
    textColor: isDarkMode ? "#ffffff" : "#000000",
    activeColor: "#10ffa8",
    hoverColor: "#8dfdd6"
  };

  return (
    <Box sx={{ backgroundColor: colors.sidebarNavBackgroundColor, color: colors.textColor }}>
      <Box
        display={'flex'}
        justifyContent={'flex-end'}
        onClick={() => onClose()}
        padding={2}
      >
        <IconButton sx={{ color: colors.textColor }}>
          <CloseIcon fontSize="small" />
        </IconButton>
      </Box>
      <Box paddingX={2} paddingBottom={2}>
        <Box>
          {pages.map((item, i) => (
            <Box key={i} marginBottom={4}>
              <Box
                display={'flex'}
                alignItems="baseline"
                component="a"
                underline="none"
                href="/"
                title="Northern Lion Labs"
                width={35}
              >
                <WebbeeLogo height={'180%'} width={'180%'} />
              </Box>
              <Typography
                variant="caption"
                sx={{
                  fontWeight: 700,
                  textTransform: 'uppercase',
                  marginBottom: 1,
                  display: 'block',
                  color: colors.textColor,
                }}
              >
                {item.title}
              </Typography>
              <Grid container spacing={1}>
                {item.pages.map((p, i) => (
                  <Grid item xs={6} key={i}>
                    <Link
                      variant="body2"
                      component={'a'}
                      href={p.href}
                      underline={'none'}
                      sx={{
                        fontWeight: activeLink === p.href ? 600 : 400,
                        color: activeLink === p.href ? colors.activeColor : colors.textColor,
                        '&:hover': {
                          textDecoration: 'none',
                          color: colors.hoverColor,
                        },
                      }}
                      onClick={() => onClose()}
                    >
                      {p.title}
                    </Link>
                  </Grid>
                ))}
              </Grid>
            </Box>
          ))}
        </Box>
      </Box>
    </Box>
  );
};

SidebarNav.propTypes = {
  pages: PropTypes.array.isRequired,
  onClose: PropTypes.func,
};

export default SidebarNav;
